mapper.select:
SELECT t.COMPANY_ID, t.COMPANY_NAME, t.BUDGET_ACCOUNT_CODE, t.BUDGET_ACCOUNT_NAME, t.TRANSMIT_BUDGET_AMOUNT, t.APPROVED_BUDGET_AMOUNT, CASE WHEN t.TRANSMIT_BUDGET_AMOUNT=0 OR t.APPROVED_BUDGET_AMOUNT=0 THEN 0 ELSE CAST((10000*t.APPROVED_BUDGET_AMOUNT/t.TRANSMIT_BUDGET_AMOUNT)*0.01 AS DECIMAL(30,2)) END AS APPROVED_PRO, t.OCCUPIED_BUDGET_AMOUNT_SUM, CASE WHEN t.APPROVED_BUDGET_AMOUNT=0 OR t.OCCUPIED_BUDGET_AMOUNT_SUM=0 THEN 0 ELSE CAST((10000*t.OCCUPIED_BUDGET_AMOUNT_SUM/t.APPROVED_BUDGET_AMOUNT)*0.01 AS DECIMAL(30,2)) END AS OCCUPIED_PRO FROM ( SELECT ab.COMPANY_ID, ab.COMPANY_NAME, ab.BUDGET_ACCOUNT_CODE, ab.BUDGET_ACCOUNT_NAME, CAST(COALESCE(ab.TRANSMIT_BUDGET_AMOUNT,0) AS DECIMAL(30,2)) TRANSMIT_BUDGET_AMOUNT, CASE WHEN ab.BUDGET_ACCOUNT_CODE='EBPL01010302' THEN CAST(COALESCE(ac.APPROVED_BUDGET_AMOUNT1,0) AS DECIMAL(30,2)) ELSE CAST(COALESCE(ab.APPROVED_BUDGET_AMOUNT,0) AS DECIMAL(30,2)) END AS APPROVED_BUDGET_AMOUNT, CASE WHEN ab.BUDGET_ACCOUNT_CODE='EBPL01010302' THEN CAST(COALESCE(ac.OCCUPIED_BUDGET_AMOUNT_SUM1,0) AS DECIMAL(30,2)) ELSE CAST(COALESCE(ab.OCCUPIED_BUDGET_AMOUNT_SUM,0) AS DECIMAL(30,2)) END AS OCCUPIED_BUDGET_AMOUNT_SUM FROM ( SELECT pa.COMPANY_ID, tc.company_code, pa.budget_year, tc.COMPANY_NAME, pa.BUDGET_ACCOUNT_CODE, pa.BUDGET_ACCOUNT_NAME, 0 TRANSMIT_BUDGET_AMOUNT, pa.APPROVED_BUDGET_AMOUNT, pa.RESERVED_BUDGET_AMOUNT_SUM, pa.OCCUPIED_BUDGET_AMOUNT_SUM FROM ( SELECT a.BUDGET_YEAR, a.COMPANY_NATURE_LOOKUP_CODE, a.SET_OF_BOOKS_ID, a.COMPANY_ID, a.BUDGET_TYPE_LOOKUP_CODE , c.BUDGET_ACCOUNT_ID, MAX(c.BUDGET_ACCOUNT_NAME) BUDGET_ACCOUNT_NAME, MAX(c.BUDGET_ACCOUNT_CODE) BUDGET_ACCOUNT_CODE, ROUND(SUM( CASE WHEN COALESCE(a.APPROVED_BUDGET_AMOUNT, 0) = 0 THEN MIN(COALESCE(a.INPROCESS_BUDGET_AMOUNT, 0), COALESCE (a.OLD_AMOUNT , 0)) ELSE a.APPROVED_BUDGET_AMOUNT END ),2) APPROVED_BUDGET_AMOUNT, SUM(a.RESERVED_BUDGET_AMOUNT_SUM) RESERVED_BUDGET_AMOUNT_SUM, SUM(a.OCCUPIED_BUDGET_AMOUNT_SUM) OCCUPIED_BUDGET_AMOUNT_SUM FROM tbm_budget_project_summary a , TBM_DIMVAL_SUB_COMBINATIONS b , TBM_BUDGET_ACCOUNTS c WHERE 1=1 AND a.DIMVAL_SUB_COMBINATION_ID=b.DIMVAL_SUB_COMBINATION_ID AND b.BUDGET_ACCOUNT_ID=c.BUDGET_ACCOUNT_ID AND a.BUDGET_TYPE_LOOKUP_CODE = 'OPEX' AND c.BUDGET_ACCOUNT_CODE LIKE 'PL02050101__' GROUP BY a.BUDGET_YEAR, a.COMPANY_NATURE_LOOKUP_CODE, a.SET_OF_BOOKS_ID, a.COMPANY_ID, a.BUDGET_TYPE_LOOKUP_CODE, c.BUDGET_ACCOUNT_ID)pa INNER JOIN TBM_COMPANIES tc ON tc.COMPANY_ID = pa.COMPANY_ID UNION SELECT a.COMPANY_ID, tc.company_code, a.budget_year, tc.COMPANY_NAME, c.BUDGET_ACCOUNT_CODE, MAX(c.BUDGET_ACCOUNT_NAME) BUDGET_ACCOUNT_NAME, SUM(a.TRANSMIT_BUDGET_AMOUNT) TRANSMIT_BUDGET_AMOUNT, 0 APPROVED_BUDGET_AMOUNT, 0 RESERVED_BUDGET_AMOUNT_SUM, 0 OCCUPIED_BUDGET_AMOUNT_SUM FROM TBM_BUDGET_ANNUAL_DIMVAL_SUMMARY a INNER JOIN TBM_DIMVAL_SUB_COMBINATIONS b ON a.DIMVAL_SUB_COMBINATION_ID=b.DIMVAL_SUB_COMBINATION_ID INNER JOIN TBM_BUDGET_ACCOUNTS c ON b.BUDGET_ACCOUNT_ID=c.BUDGET_ACCOUNT_ID INNER JOIN TBM_COMPANIES tc ON tc.COMPANY_ID = a.COMPANY_ID WHERE c.BUDGET_ACCOUNT_CODE='EBPL01010302' AND a.BUDGET_TYPE_LOOKUP_CODE = 'OPEX' AND a.BUDGET_VERSION_TYPE = 'Z' GROUP BY a.budget_year, a.COMPANY_ID, tc.company_code, tc.COMPANY_NAME, c.BUDGET_ACCOUNT_CODE)ab LEFT JOIN ( SELECT a.COMPANY_ID, ROUND(SUM( CASE WHEN COALESCE(a.APPROVED_BUDGET_AMOUNT, 0) = 0 THEN MIN(COALESCE(a.INPROCESS_BUDGET_AMOUNT, 0), COALESCE(a.OLD_AMOUNT, 0)) ELSE a.APPROVED_BUDGET_AMOUNT END ),2) APPROVED_BUDGET_AMOUNT1, SUM(a.RESERVED_BUDGET_AMOUNT_SUM) RESERVED_BUDGET_AMOUNT_SUM1, SUM(a.OCCUPIED_BUDGET_AMOUNT_SUM) OCCUPIED_BUDGET_AMOUNT_SUM1 FROM tbm_budget_project_summary a , TBM_DIMVAL_SUB_COMBINATIONS b , TBM_BUDGET_ACCOUNTS c WHERE 1=1 AND a.BUDGET_YEAR=${budget_year} AND a.DIMVAL_SUB_COMBINATION_ID=b.DIMVAL_SUB_COMBINATION_ID AND b.BUDGET_ACCOUNT_ID=c.BUDGET_ACCOUNT_ID AND a.BUDGET_TYPE_LOOKUP_CODE = 'OPEX' AND c.BUDGET_ACCOUNT_CODE LIKE 'PL02050101__' GROUP BY a.COMPANY_ID )ac ON ac.company_id=ab.company_id LEFT JOIN ( SELECT a.COMPANY_ID, MAX(tc.COMPANY_NAME) COMPANY_NAME, c.BUDGET_ACCOUNT_CODE, MAX(c.BUDGET_ACCOUNT_NAME) BUDGET_ACCOUNT_NAME, SUM(a.TRANSMIT_BUDGET_AMOUNT) TRANSMIT_BUDGET_AMOUNT1 FROM TBM_BUDGET_ANNUAL_DIMVAL_SUMMARY a INNER JOIN TBM_DIMVAL_SUB_COMBINATIONS b ON a.DIMVAL_SUB_COMBINATION_ID=b.DIMVAL_SUB_COMBINATION_ID INNER JOIN TBM_BUDGET_ACCOUNTS c ON b.BUDGET_ACCOUNT_ID=c.BUDGET_ACCOUNT_ID INNER JOIN TBM_COMPANIES tc ON tc.COMPANY_ID = a.COMPANY_ID WHERE c.BUDGET_ACCOUNT_CODE='EBPL01010302' AND a.budget_year=${budget_year} AND a.BUDGET_TYPE_LOOKUP_CODE = 'OPEX' AND a.BUDGET_VERSION_TYPE = 'Z' GROUP BY a.budget_year, a.COMPANY_ID, c.BUDGET_ACCOUNT_CODE)ad ON ad.COMPANY_ID=ab.COMPANY_ID WHERE ab.BUDGET_YEAR=${budget_year}
AND ab.COMPANY_CODE in (${companycodes})
AND ab.COMPANY_CODE in ('')
) t ORDER BY t.COMPANY_ID, t.BUDGET_ACCOUNT_CODE

SELECT COMPANY_CODE
  FROM tbm_companies tc
 WHERE COMPANY_ID=${company_id}

SELECT COMPANY_CODE,COMPANY_NAME
  FROM tbm_companies tc
 WHERE COMPANY_ID=${company_id}

SELECT org_ext_id value,org_name name
  FROM arch_org_ext
 WHERE org_code=#{org_id}

SELECT count(*)
  FROM tbm_budget_complete_status_data d
  WHERE d.PERIOD_NAME = #{period_name}
